import {
  Component,
  EventEmitter,
  Input,
  Output,
  OnChanges,
  SimpleChanges,
  OnInit,
} from '@angular/core';
import { random, sample, times } from 'lodash';

export interface AlertMessagObj {
  message: string;
}

@Component({
  selector: 'app-alert',
  templateUrl: './alert.component.html',
  styleUrls: ['./alert.component.scss'],
  standalone: true,
})
export class AlertComponent implements OnInit, OnChanges {
  @Input() alertMessage: AlertMessagObj | null = null;
  @Output() alertClosed = new EventEmitter<boolean>();

  showAlert: boolean = false;

  uniqId: string = '';

  timeout: number = NaN;

  generateRandomString(length: number) {
    return times(length, () => random(35).toString(36)).join('');
  }

  hideAlert() {
    this.showAlert = false;

    this.uniqId = '';

    this.alertClosed.emit(true);
  }

  ngOnChanges(changes: SimpleChanges): void {
    this.uniqId = this.generateRandomString(12);

    this.showAlert = true;

    if (this.timeout) {
      clearTimeout(this.timeout);
    }

    this.timeout = setTimeout(() => {
      this.showAlert = false;
    }, 5000) as unknown as number;
  }

  ngOnInit(): void {
    this.showAlert = false;
  }
}

import { Injectable } from '@angular/core';
import Color from 'colorjs.io';
import { to } from 'colorjs.io/fn';
import { ColorConstructor } from 'colorjs.io/types/src/color';
import { scaleLinear, sort } from 'd3';
import { random, reverse, uniqBy, uniq } from 'lodash';
import {
  TableColorCell,
  TableData,
} from '../palette-table/palette-table.component';

export type ColorPair = [string, string];

export type ColorCoordArray = [number, number, number];

export class ChromaMatchObject {
  success: boolean = false;
  colors: ColorPair | null = null;
  chroma: number | null = null;
}

export interface MinMaxLightObject {
  originalCoords: ColorCoordArray;
  lightMin: number;
  lightMax: number;
}

// export type ColorVariant = [number, number, number];

export interface ColorMetaObj {
  lightness: number | string;
  chroma: number | string;
  hue: number | string;
  saturation: number | string;
}

@Injectable({
  providedIn: 'root',
})
export class ColorUtilService {
  parseColor(color: string): ColorConstructor | null {
    let parsedColor: ColorConstructor | null = null;

    try {
      parsedColor = Color.parse(color);
    } catch (error) {
      console.error(error);
    }
    return parsedColor;
  }

  getRgb255Array(color: string) {
    const colorObj = this.parseColor(color);

    let array255: [number, number, number] | null = null;

    if (colorObj) {
      const colorCoordsDecimal = colorObj.coords;

      array255 = [
        Math.round(colorCoordsDecimal[0] * 255),
        Math.round(colorCoordsDecimal[1] * 255),
        Math.round(colorCoordsDecimal[2] * 255),
      ];
    } else {
      console.error(`unable to parse: ${color}`);
    }

    return array255;
  }

  createSrgbColor(color: string, lightness: number): string | null {
    let srgbColor: string | null = null;

    const parsedColor = this.parseColor(color);

    if (parsedColor) {
      const oklchColor = Color.to(parsedColor, 'oklch');
      const originalChroma = oklchColor.coords[1];
      const originalHue = oklchColor.coords[2];

      const targetColor = new Color('oklch', [
        lightness,
        originalChroma,
        originalHue,
      ]);

      // const inSrgbGamut = targetColor.inGamut('srgb');

      // if (!inSrgbGamut) {
      //   console.warn(
      //     `OKLCH color in SRGB gamut: ${targetColor.inGamut('srgb')}`
      //   );
      // }

      const tColorInSrgbGamut = targetColor.toGamut({
        space: 'srgb',
        method: 'oklch.c',
      });

      const targetColorAsRgbColor = tColorInSrgbGamut.to('srgb');

      srgbColor = targetColorAsRgbColor.toString({ format: 'hex' });
    }

    return srgbColor;
  }

  isInSrgbGamut(oklchColorCoord: ColorCoordArray): Promise<boolean> {
    return new Promise<boolean>((resolve, reject) => {
      try {
        const colorObject = new Color('oklch', oklchColorCoord);
        const variantInGamut = colorObject.inGamut('srgb');

        resolve(variantInGamut);
      } catch (error) {
        reject(error);
      }
    });
  }

  createVariants(color: string): Array<ColorCoordArray> | null {
    let variantCollection: Array<ColorCoordArray> | null = null;

    const parsedColor = this.parseColor(color);

    if (parsedColor) {
      const oklchColor = Color.to(parsedColor, 'oklch');
      const lchCooords = oklchColor.coords;
      const colorChroma = lchCooords[1];
      const colorHue = lchCooords[2];

      // 1) create enough steps of lightness
      const lightnessSteps = 1000;
      const lightMax = 1;
      const lightMin = 0;
      const lightInterval = (lightMax - lightMin) / lightnessSteps;

      // 2) create all variants of color using constant chroma and hue.
      variantCollection = [];

      // 2a) this actually creates `lightnessSteps + 1` variants as initial variant has to start at zero.
      for (let i = 0; i <= lightnessSteps; i++) {
        const variantTargetLight = i * lightInterval;
        const variant: ColorCoordArray = [
          variantTargetLight,
          colorChroma,
          colorHue,
        ];

        variantCollection.push(variant);
      }
    } else {
      console.error(`unable to parse color`);
    }

    return variantCollection;
  }

  filterOutOfGamutVariants(
    variants: Array<ColorCoordArray> | null
  ): Promise<Array<ColorCoordArray>> {
    return new Promise(async (resolve, reject) => {
      if (!variants) {
        reject(`no variants`);
      } else {
        let filteringComplete: boolean = false;

        const filtered = [];

        for (let i = 0; i < variants.length; i++) {
          const curVariant = variants[i];

          if (await this.isInSrgbGamut(curVariant)) {
            filtered.push(curVariant);
          }

          if (i === variants.length - 1) filteringComplete = true;
        }

        if (filteringComplete) {
          resolve(filtered);
        } else {
          reject(`error`);
        }
      }
    });
  }

  async getMinMaxLight(color: string): Promise<MinMaxLightObject | null> {
    let returnedObject: MinMaxLightObject | null = null;

    const initVariantCollection = this.createVariants(color);

    const variantCollection = await this.filterOutOfGamutVariants(
      initVariantCollection
    );

    const parsedColor = this.parseColor(color);

    if (parsedColor && variantCollection.length) {
      const oklchColor = Color.to(parsedColor, 'oklch');

      const lchCooords = oklchColor.coords;

      const oklchLightCoordIndex = 0;
      const firstArrayItemIndex = 0;
      const lastArrayItemIndex = variantCollection.length - 1;

      const minLight =
        variantCollection[firstArrayItemIndex][oklchLightCoordIndex];
      const maxLight =
        variantCollection[lastArrayItemIndex][oklchLightCoordIndex];

      returnedObject = {
        originalCoords: lchCooords,
        lightMin: minLight,
        lightMax: maxLight,
      };
    } else {
      if (!parsedColor) {
        console.error(`unable to parse color`);
      } else {
        const oklchColor = Color.to(parsedColor, 'oklch');

        const lchCooords = oklchColor.coords;
        returnedObject = {
          originalCoords: lchCooords,
          lightMin: lchCooords[0],
          lightMax: lchCooords[0],
        };
      }
    }

    return returnedObject;
  }

  async getRandomColorPair(): Promise<ColorPair> {
    let pair: ColorPair = ['black', 'white'];

    // ref: oklch.com
    // const targetChroma = 0.11;
    const targetChroma = random(0.11, 0.34, true);

    const colorOneLight = random(0.25, 0.26, true);
    const colorOneHue = random(0, 360, true);
    const colorTwoLight = random(0.94, 0.95, true);
    const colorTwoHue = random(0, 360, true);

    const colorOne = new Color('oklch', [
      colorOneLight,
      targetChroma,
      colorOneHue,
    ])
      .toGamut({ space: 'srgb', method: 'oklch.c' })
      .to('srgb')
      .toString({ format: 'hex' });

    const colorTwo = new Color('oklch', [
      colorTwoLight,
      targetChroma,
      colorTwoHue,
    ])
      .toGamut({ space: 'srgb', method: 'oklch.c' })
      .to('srgb')
      .toString({ format: 'hex' });

    const initPair: ColorPair = [colorOne, colorTwo];

    const chromaMatchedPair = await this.matchChromas(initPair);

    pair = chromaMatchedPair.colors ? chromaMatchedPair.colors : pair;

    return pair;
  }

  // This function only adjust the first color of the pair.
  async adjustColorPairForPresentation(pair: ColorPair): Promise<ColorPair> {
    let returnedPair: ColorPair = ['black', 'white'];

    const colorOne = pair[0];
    const colortwo = pair[1];

    const parsedColorOne = this.parseColor(colorOne);

    const colorOneMinMaxLightObj = await this.getMinMaxLight(colorOne);

    if (parsedColorOne && colorOneMinMaxLightObj) {
      const colorOneTargetLightness =
        colorOneMinMaxLightObj.lightMin +
        (colorOneMinMaxLightObj.lightMax - colorOneMinMaxLightObj.lightMin) / 2;

      const oklchColorOne = new Color('srgb', parsedColorOne.coords).to(
        'oklch'
      );

      const adjColorOne = new Color('oklch', [
        colorOneTargetLightness,
        oklchColorOne.coords[1],
        oklchColorOne.coords[2],
      ])
        .to('srgb')
        .toString({ format: 'hex' });

      returnedPair = [adjColorOne, colortwo];
    } else {
      console.warn('trouble adjusting colors');
    }

    return returnedPair;
  }

  async matchChromas(colorpair: ColorPair): Promise<ChromaMatchObject> {
    let pair: ChromaMatchObject = {
      success: false,
      colors: null,
      chroma: null,
    };

    const colorOneParsed = this.parseColor(colorpair[0]);
    const colorTwoParsed = this.parseColor(colorpair[1]);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneOklch = new Color('srgb', colorOneParsed.coords).to(
        'oklch'
      );
      const colorOneChroma = colorOneOklch.coords[1];

      const colorTwoOklch = new Color('srgb', colorTwoParsed.coords).to(
        'oklch'
      );
      const colorTwoChroma = colorTwoOklch.coords[1];

      const colorOneCandCoords: ColorCoordArray = [
        colorOneOklch.coords[0],
        colorTwoChroma,
        colorOneOklch.coords[2],
      ];
      const colorTwoCandCoords: ColorCoordArray = [
        colorTwoOklch.coords[0],
        colorOneChroma,
        colorTwoOklch.coords[2],
      ];

      const colorOneCandInGamut = await this.isInSrgbGamut(colorOneCandCoords);
      const colorTwoCandInGamut = await this.isInSrgbGamut(colorTwoCandCoords);

      if (colorOneCandInGamut && !colorTwoCandInGamut) {
        pair.success = true;

        pair.colors = [
          new Color('oklch', colorOneCandCoords)
            .to('srgb')
            .toString({ format: 'hex' }),
          colorpair[1],
        ];

        pair.chroma = colorOneCandCoords[1];
      }

      if (!colorOneCandInGamut && colorTwoCandInGamut) {
        pair.success = true;

        pair.colors = [
          colorpair[0],
          new Color('oklch', colorTwoCandCoords)
            .to('srgb')
            .toString({ format: 'hex' }),
        ];

        pair.chroma = colorTwoCandCoords[1];
      }

      if (colorOneCandInGamut && colorTwoCandInGamut) {
        if (colorOneCandCoords[1] > colorTwoCandCoords[1]) {
          pair.success = true;

          pair.colors = [
            new Color('oklch', colorOneCandCoords)
              .to('srgb')
              .toString({ format: 'hex' }),
            colorpair[1],
          ];

          pair.chroma = colorTwoCandCoords[1];
        } else {
          pair.success = true;

          pair.colors = [
            colorpair[0],
            new Color('oklch', colorTwoCandCoords)
              .to('srgb')
              .toString({ format: 'hex' }),
          ];

          pair.chroma = colorTwoCandCoords[1];
        }
      }
    } else {
      console.error("color parsing didn't work out. ");
    }

    return pair;
  }

  calcDeltaE(colorOne: string, colorTwo: string): number | null {
    let delta: number | null = null;

    const colorOneParsed = this.parseColor(colorOne);
    const colorTwoParsed = this.parseColor(colorTwo);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneObj = new Color('srgb', colorOneParsed.coords);
      const colorTwoObj = new Color('srgb', colorTwoParsed.coords);

      const rawDelta = colorOneObj.deltaE2000(colorTwoObj);

      // const fixedDelta = rawDelta.toFixed(2);

      // delta = parseFloat(fixedDelta);
      delta = Math.round(rawDelta);
    }

    return delta;
  }

  calcWcag2(colorOne: string, colorTwo: string): number | null {
    let wcag21: number | null = null;

    const colorOneParsed = this.parseColor(colorOne);
    const colorTwoParsed = this.parseColor(colorTwo);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneObj = new Color('srgb', colorOneParsed.coords);
      const colorTwoObj = new Color('srgb', colorTwoParsed.coords);

      const rawWcag21 = colorOneObj.contrast(colorTwoObj, 'WCAG21');

      const fixedDelta = rawWcag21.toFixed(1);

      wcag21 = parseFloat(fixedDelta);
    }

    return wcag21;
  }

  getColorMeta(color: string): ColorMetaObj | null {
    let meta: ColorMetaObj | null = null;

    const parsedColor = this.parseColor(color);

    if (parsedColor) {
      const lchColor = Color.to(parsedColor, 'oklch');

      meta = {
        lightness: lchColor.coords[0].toFixed(2),
        chroma: lchColor.coords[1].toFixed(2),
        hue: lchColor.coords[2].toFixed(2),
        saturation: ((lchColor.coords[1] / lchColor.coords[0]) * 100).toFixed(
          2
        ),
      };
    }

    return meta;
  }

  getMinObjectDimension(apca: number): number {
    let dimension: number = NaN;

    const absApca = Math.abs(apca);

    const pixels = [1, 1.5, 2, 3, 4, 6, 8, 10, 15];
    const apcaScores = [90, 75, 60, 50, 45, 30, 25, 20, 15];
    const minLookup = scaleLinear(pixels).domain(apcaScores);

    const initSize = minLookup(absApca).toFixed(2);

    dimension = parseFloat(initSize);

    if (dimension > 15) {
      dimension = 15;
    }

    if (absApca >= 100) {
      dimension = 0.25;
    }

    if (absApca < 15) {
      dimension = NaN;
    }

    return dimension;
  }

  generateAllOklchVariants(
    color: string,
    lightSteps: number,
    chromaSteps: number
  ): Promise<TableData> {
    return new Promise((resolve, reject) => {
      const parsedColor = this.parseColor(color);

      const variantsCollection = [];

      let sortedVariantsCollection = [];

      if (parsedColor) {
        const oklchColor = Color.to(parsedColor, 'oklch');
        const lchCooords = oklchColor.coords;
        const colorLight = lchCooords[0];
        const colorChroma = lchCooords[1];
        const colorHue = lchCooords[2];

        const lightMax = 1;
        const lightMin = 0;
        const lightInterval = (lightMax - lightMin) / lightSteps;

        // calculate light levels
        const rawLightLevels = [];

        let lLevel = colorLight;
        do {
          rawLightLevels.push(lLevel);

          lLevel = lLevel + lightInterval;
        } while (lLevel <= lightMax);

        lLevel = colorLight;

        do {
          rawLightLevels.push(lLevel);

          lLevel = lLevel - lightInterval;
        } while (lLevel >= lightMin);

        const sortedLightLevels = sort(uniq(rawLightLevels));

        const chromaMax = 0.33;
        const chromaMin = 0;
        const chromaInterval = (chromaMax - chromaMin) / chromaSteps;

        // calculate chroma levels
        const rawChromaLevels = [];

        let cLevel = colorChroma;
        do {
          rawChromaLevels.push(cLevel);

          cLevel = cLevel + chromaInterval;
        } while (cLevel <= chromaMax);

        cLevel = colorChroma;

        do {
          rawChromaLevels.push(cLevel);

          cLevel = cLevel - chromaInterval;
        } while (cLevel >= chromaMin);

        const sortedChromaLevels = sort(uniq(rawChromaLevels));

        // generate all rows
        for (let i = 0; i < sortedLightLevels.length; i++) {
          const variantRow: Array<TableColorCell> = [];

          for (let j = 0; j < sortedChromaLevels.length; j++) {
            const targetLightness = sortedLightLevels[i];

            const targetChroma = sortedChromaLevels[j];

            const variantColor = new Color('oklch', [
              targetLightness,
              targetChroma,
              colorHue,
            ]);

            const variantColorinGamut = variantColor.inGamut('srgb');

            const colorVal = variantColorinGamut
              ? variantColor.to('srgb').toString({ format: 'hex' })
              : null;

            const deltaE = colorVal ? this.calcDeltaE(colorVal, color) : null;

            const dLight = colorVal
              ? Math.round(((targetLightness - colorLight) / colorLight) * 100)
              : null;

            const dChroma = colorVal
              ? Math.round(((targetChroma - colorChroma) / colorChroma) * 100)
              : null;

            const variantObj: TableColorCell = {
              color: colorVal,
              lightness: targetLightness,
              chroma: targetChroma,
              hue: colorHue,
              deltaE: deltaE,
              deltaChroma: dChroma,
              deltaLightness: dLight,
              // wacg2Comp: NaN,
              // pContrast: NaN,
            };

            variantRow.push(variantObj);
          }

          variantsCollection.push(variantRow);
        }
      } else {
        console.error(`could not parse color`);

        reject(`could not parse color`);
      }

      // order color rows from light to dark
      sortedVariantsCollection = reverse(variantsCollection);

      resolve(sortedVariantsCollection);
    });
  }

  constructor() {}
}

import { Injectable } from '@angular/core';
import Color from 'colorjs.io';
import { to } from 'colorjs.io/fn';
import { ColorConstructor } from 'colorjs.io/types/src/color';
import { scaleLinear, sort } from 'd3';
import { random, reverse, uniqBy, uniq } from 'lodash';
import {
  TableColorCell,
  TableData,
} from '../palette-table/palette-table.component';

export type ColorPair = [string, string];

export type ColorCoordArray = [number, number, number];

export class ChromaMatchObject {
  success: boolean = false;
  colors: ColorPair | null = null;
  chroma: number | null = null;
}

export interface MinMaxLightObject {
  originalCoords: ColorCoordArray;
  lightMin: number;
  lightMax: number;
}

// export type ColorVariant = [number, number, number];

export interface ColorMetaObj {
  lightness: number | string;
  chroma: number | string;
  hue: number | string;
  saturation: number | string;
}

@Injectable({
  providedIn: 'root',
})
export class ColorUtilService {
  parseColor(color: string): ColorConstructor | null {
    let parsedColor: ColorConstructor | null = null;

    try {
      parsedColor = Color.parse(color);
    } catch (error) {
      console.error(error);
    }
    return parsedColor;
  }

  getRgb255Array(color: string) {
    const colorObj = this.parseColor(color);

    let array255: [number, number, number] | null = null;

    if (colorObj) {
      const colorCoordsDecimal = colorObj.coords;

      array255 = [
        Math.round(colorCoordsDecimal[0] * 255),
        Math.round(colorCoordsDecimal[1] * 255),
        Math.round(colorCoordsDecimal[2] * 255),
      ];
    } else {
      console.error(`unable to parse: ${color}`);
    }

    return array255;
  }

  createSrgbColor(color: string, lightness: number): string | null {
    let srgbColor: string | null = null;

    const parsedColor = this.parseColor(color);

    if (parsedColor) {
      const oklchColor = Color.to(parsedColor, 'oklch');
      const originalChroma = oklchColor.coords[1];
      const originalHue = oklchColor.coords[2];

      const targetColor = new Color('oklch', [
        lightness,
        originalChroma,
        originalHue,
      ]);

      // const inSrgbGamut = targetColor.inGamut('srgb');

      // if (!inSrgbGamut) {
      //   console.warn(
      //     `OKLCH color in SRGB gamut: ${targetColor.inGamut('srgb')}`
      //   );
      // }

      const tColorInSrgbGamut = targetColor.toGamut({
        space: 'srgb',
        method: 'oklch.c',
      });

      const targetColorAsRgbColor = tColorInSrgbGamut.to('srgb');

      srgbColor = targetColorAsRgbColor.toString({ format: 'hex' });
    }

    return srgbColor;
  }

  isInSrgbGamut(oklchColorCoord: ColorCoordArray): Promise<boolean> {
    return new Promise<boolean>((resolve, reject) => {
      try {
        const colorObject = new Color('oklch', oklchColorCoord);
        const variantInGamut = colorObject.inGamut('srgb');

        resolve(variantInGamut);
      } catch (error) {
        reject(error);
      }
    });
  }

  createVariants(color: string): Array<ColorCoordArray> | null {
    let variantCollection: Array<ColorCoordArray> | null = null;

    const parsedColor = this.parseColor(color);

    if (parsedColor) {
      const oklchColor = Color.to(parsedColor, 'oklch');
      const lchCooords = oklchColor.coords;
      const colorChroma = lchCooords[1];
      const colorHue = lchCooords[2];

      // 1) create enough steps of lightness
      const lightnessSteps = 1000;
      const lightMax = 1;
      const lightMin = 0;
      const lightInterval = (lightMax - lightMin) / lightnessSteps;

      // 2) create all variants of color using constant chroma and hue.
      variantCollection = [];

      // 2a) this actually creates `lightnessSteps + 1` variants as initial variant has to start at zero.
      for (let i = 0; i <= lightnessSteps; i++) {
        const variantTargetLight = i * lightInterval;
        const variant: ColorCoordArray = [
          variantTargetLight,
          colorChroma,
          colorHue,
        ];

        variantCollection.push(variant);
      }
    } else {
      console.error(`unable to parse color`);
    }

    return variantCollection;
  }

  filterOutOfGamutVariants(
    variants: Array<ColorCoordArray> | null
  ): Promise<Array<ColorCoordArray>> {
    return new Promise(async (resolve, reject) => {
      if (!variants) {
        reject(`no variants`);
      } else {
        let filteringComplete: boolean = false;

        const filtered = [];

        for (let i = 0; i < variants.length; i++) {
          const curVariant = variants[i];

          if (await this.isInSrgbGamut(curVariant)) {
            filtered.push(curVariant);
          }

          if (i === variants.length - 1) filteringComplete = true;
        }

        if (filteringComplete) {
          resolve(filtered);
        } else {
          reject(`error`);
        }
      }
    });
  }

  async getMinMaxLight(color: string): Promise<MinMaxLightObject | null> {
    let returnedObject: MinMaxLightObject | null = null;

    const initVariantCollection = this.createVariants(color);

    const variantCollection = await this.filterOutOfGamutVariants(
      initVariantCollection
    );

    const parsedColor = this.parseColor(color);

    if (parsedColor && variantCollection.length) {
      const oklchColor = Color.to(parsedColor, 'oklch');

      const lchCooords = oklchColor.coords;

      const oklchLightCoordIndex = 0;
      const firstArrayItemIndex = 0;
      const lastArrayItemIndex = variantCollection.length - 1;

      const minLight =
        variantCollection[firstArrayItemIndex][oklchLightCoordIndex];
      const maxLight =
        variantCollection[lastArrayItemIndex][oklchLightCoordIndex];

      returnedObject = {
        originalCoords: lchCooords,
        lightMin: minLight,
        lightMax: maxLight,
      };
    } else {
      if (!parsedColor) {
        console.error(`unable to parse color`);
      } else {
        const oklchColor = Color.to(parsedColor, 'oklch');

        const lchCooords = oklchColor.coords;
        returnedObject = {
          originalCoords: lchCooords,
          lightMin: lchCooords[0],
          lightMax: lchCooords[0],
        };
      }
    }

    return returnedObject;
  }

  async getRandomColorPair(): Promise<ColorPair> {
    let pair: ColorPair = ['black', 'white'];

    // ref: oklch.com
    // const targetChroma = 0.11;
    const targetChroma = random(0.11, 0.34, true);

    const colorOneLight = random(0.25, 0.26, true);
    const colorOneHue = random(0, 360, true);
    const colorTwoLight = random(0.94, 0.95, true);
    const colorTwoHue = random(0, 360, true);

    const colorOne = new Color('oklch', [
      colorOneLight,
      targetChroma,
      colorOneHue,
    ])
      .toGamut({ space: 'srgb', method: 'oklch.c' })
      .to('srgb')
      .toString({ format: 'hex' });

    const colorTwo = new Color('oklch', [
      colorTwoLight,
      targetChroma,
      colorTwoHue,
    ])
      .toGamut({ space: 'srgb', method: 'oklch.c' })
      .to('srgb')
      .toString({ format: 'hex' });

    const initPair: ColorPair = [colorOne, colorTwo];

    const chromaMatchedPair = await this.matchChromas(initPair);

    pair = chromaMatchedPair.colors ? chromaMatchedPair.colors : pair;

    return pair;
  }

  // This function only adjust the first color of the pair.
  async adjustColorPairForPresentation(pair: ColorPair): Promise<ColorPair> {
    let returnedPair: ColorPair = ['black', 'white'];

    const colorOne = pair[0];
    const colortwo = pair[1];

    const parsedColorOne = this.parseColor(colorOne);

    const colorOneMinMaxLightObj = await this.getMinMaxLight(colorOne);

    if (parsedColorOne && colorOneMinMaxLightObj) {
      const colorOneTargetLightness =
        colorOneMinMaxLightObj.lightMin +
        (colorOneMinMaxLightObj.lightMax - colorOneMinMaxLightObj.lightMin) / 2;

      const oklchColorOne = new Color('srgb', parsedColorOne.coords).to(
        'oklch'
      );

      const adjColorOne = new Color('oklch', [
        colorOneTargetLightness,
        oklchColorOne.coords[1],
        oklchColorOne.coords[2],
      ])
        .to('srgb')
        .toString({ format: 'hex' });

      returnedPair = [adjColorOne, colortwo];
    } else {
      console.warn('trouble adjusting colors');
    }

    return returnedPair;
  }

  async matchChromas(colorpair: ColorPair): Promise<ChromaMatchObject> {
    let pair: ChromaMatchObject = {
      success: false,
      colors: null,
      chroma: null,
    };

    const colorOneParsed = this.parseColor(colorpair[0]);
    const colorTwoParsed = this.parseColor(colorpair[1]);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneOklch = new Color('srgb', colorOneParsed.coords).to(
        'oklch'
      );
      const colorOneChroma = colorOneOklch.coords[1];

      const colorTwoOklch = new Color('srgb', colorTwoParsed.coords).to(
        'oklch'
      );
      const colorTwoChroma = colorTwoOklch.coords[1];

      const colorOneCandCoords: ColorCoordArray = [
        colorOneOklch.coords[0],
        colorTwoChroma,
        colorOneOklch.coords[2],
      ];
      const colorTwoCandCoords: ColorCoordArray = [
        colorTwoOklch.coords[0],
        colorOneChroma,
        colorTwoOklch.coords[2],
      ];

      const colorOneCandInGamut = await this.isInSrgbGamut(colorOneCandCoords);
      const colorTwoCandInGamut = await this.isInSrgbGamut(colorTwoCandCoords);

      if (colorOneCandInGamut && !colorTwoCandInGamut) {
        pair.success = true;

        pair.colors = [
          new Color('oklch', colorOneCandCoords)
            .to('srgb')
            .toString({ format: 'hex' }),
          colorpair[1],
        ];

        pair.chroma = colorOneCandCoords[1];
      }

      if (!colorOneCandInGamut && colorTwoCandInGamut) {
        pair.success = true;

        pair.colors = [
          colorpair[0],
          new Color('oklch', colorTwoCandCoords)
            .to('srgb')
            .toString({ format: 'hex' }),
        ];

        pair.chroma = colorTwoCandCoords[1];
      }

      if (colorOneCandInGamut && colorTwoCandInGamut) {
        if (colorOneCandCoords[1] > colorTwoCandCoords[1]) {
          pair.success = true;

          pair.colors = [
            new Color('oklch', colorOneCandCoords)
              .to('srgb')
              .toString({ format: 'hex' }),
            colorpair[1],
          ];

          pair.chroma = colorTwoCandCoords[1];
        } else {
          pair.success = true;

          pair.colors = [
            colorpair[0],
            new Color('oklch', colorTwoCandCoords)
              .to('srgb')
              .toString({ format: 'hex' }),
          ];

          pair.chroma = colorTwoCandCoords[1];
        }
      }
    } else {
      console.error("color parsing didn't work out. ");
    }

    return pair;
  }

  calcDeltaE(colorOne: string, colorTwo: string): number | null {
    let delta: number | null = null;

    const colorOneParsed = this.parseColor(colorOne);
    const colorTwoParsed = this.parseColor(colorTwo);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneObj = new Color('srgb', colorOneParsed.coords);
      const colorTwoObj = new Color('srgb', colorTwoParsed.coords);

      const rawDelta = colorOneObj.deltaE2000(colorTwoObj);

      // const fixedDelta = rawDelta.toFixed(2);

      // delta = parseFloat(fixedDelta);
      delta = Math.round(rawDelta);
    }

    return delta;
  }

  calcWcag2(colorOne: string, colorTwo: string): number | null {
    let wcag21: number | null = null;

    const colorOneParsed = this.parseColor(colorOne);
    const colorTwoParsed = this.parseColor(colorTwo);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneObj = new Color('srgb', colorOneParsed.coords);
      const colorTwoObj = new Color('srgb', colorTwoParsed.coords);

      const rawWcag21 = colorOneObj.contrast(colorTwoObj, 'WCAG21');

      const fixedDelta = rawWcag21.toFixed(1);

      wcag21 = parseFloat(fixedDelta);
    }

    return wcag21;
  }

  getColorMeta(color: string): ColorMetaObj | null {
    let meta: ColorMetaObj | null = null;

    const parsedColor = this.parseColor(color);

    if (parsedColor) {
      const lchColor = Color.to(parsedColor, 'oklch');

      meta = {
        lightness: lchColor.coords[0].toFixed(2),
        chroma: lchColor.coords[1].toFixed(2),
        hue: lchColor.coords[2].toFixed(2),
        saturation: ((lchColor.coords[1] / lchColor.coords[0]) * 100).toFixed(
          2
        ),
      };
    }

    return meta;
  }

  getMinObjectDimension(apca: number): number {
    let dimension: number = NaN;

    const absApca = Math.abs(apca);

    const pixels = [1, 1.5, 2, 3, 4, 6, 8, 10, 15];
    const apcaScores = [90, 75, 60, 50, 45, 30, 25, 20, 15];
    const minLookup = scaleLinear(pixels).domain(apcaScores);

    const initSize = minLookup(absApca).toFixed(2);

    dimension = parseFloat(initSize);

    if (dimension > 15) {
      dimension = 15;
    }

    if (absApca >= 100) {
      dimension = 0.25;
    }

    if (absApca < 15) {
      dimension = NaN;
    }

    return dimension;
  }

  generateAllOklchVariants(
    color: string,
    lightSteps: number,
    chromaSteps: number
  ): Promise<TableData> {
    return new Promise((resolve, reject) => {
      const parsedColor = this.parseColor(color);

      const variantsCollection = [];

      let sortedVariantsCollection = [];

      if (parsedColor) {
        const oklchColor = Color.to(parsedColor, 'oklch');
        const lchCooords = oklchColor.coords;
        const colorLight = lchCooords[0];
        const colorChroma = lchCooords[1];
        const colorHue = lchCooords[2];

        const lightMax = 1;
        const lightMin = 0;
        const lightInterval = (lightMax - lightMin) / lightSteps;

        // calculate light levels
        const rawLightLevels = [];

        let lLevel = colorLight;
        do {
          rawLightLevels.push(lLevel);

          lLevel = lLevel + lightInterval;
        } while (lLevel <= lightMax);

        lLevel = colorLight;

        do {
          rawLightLevels.push(lLevel);

          lLevel = lLevel - lightInterval;
        } while (lLevel >= lightMin);

        const sortedLightLevels = sort(uniq(rawLightLevels));

        const chromaMax = 0.33;
        const chromaMin = 0;
        const chromaInterval = (chromaMax - chromaMin) / chromaSteps;

        // calculate chroma levels
        const rawChromaLevels = [];

        let cLevel = colorChroma;
        do {
          rawChromaLevels.push(cLevel);

          cLevel = cLevel + chromaInterval;
        } while (cLevel <= chromaMax);

        cLevel = colorChroma;

        do {
          rawChromaLevels.push(cLevel);

          cLevel = cLevel - chromaInterval;
        } while (cLevel >= chromaMin);

        const sortedChromaLevels = sort(uniq(rawChromaLevels));

        // generate all rows
        for (let i = 0; i < sortedLightLevels.length; i++) {
          const variantRow: Array<TableColorCell> = [];

          for (let j = 0; j < sortedChromaLevels.length; j++) {
            const targetLightness = sortedLightLevels[i];

            const targetChroma = sortedChromaLevels[j];

            const variantColor = new Color('oklch', [
              targetLightness,
              targetChroma,
              colorHue,
            ]);

            const variantColorinGamut = variantColor.inGamut('srgb');

            const colorVal = variantColorinGamut
              ? variantColor.to('srgb').toString({ format: 'hex' })
              : null;

            const deltaE = colorVal ? this.calcDeltaE(colorVal, color) : null;

            const dLight = colorVal
              ? Math.round(((targetLightness - colorLight) / colorLight) * 100)
              : null;

            const dChroma = colorVal
              ? Math.round(((targetChroma - colorChroma) / colorChroma) * 100)
              : null;

            const variantObj: TableColorCell = {
              color: colorVal,
              lightness: targetLightness,
              chroma: targetChroma,
              hue: colorHue,
              deltaE: deltaE,
              deltaChroma: dChroma,
              deltaLightness: dLight,
              // wacg2Comp: NaN,
              // pContrast: NaN,
            };

            variantRow.push(variantObj);
          }

          variantsCollection.push(variantRow);
        }
      } else {
        console.error(`could not parse color`);

        reject(`could not parse color`);
      }

      // order color rows from light to dark
      sortedVariantsCollection = reverse(variantsCollection);

      resolve(sortedVariantsCollection);
    });
  }

  constructor() {}
}

import { Injectable, inject } from '@angular/core';

import { calcAPCA, sRGBtoY } from 'apca-w3';

import { scaleLinear } from 'd3';

import { ColorUtilService } from './color-util.service';
import { BpcaService } from './bpca.service';

export interface NumberKeyLookup {
  [key: number]: number;
}

export type ContrastType = 'apca' | 'bpca';
@Injectable({
  providedIn: 'root',
})
export class ColorMetricsService {
  cus = inject(ColorUtilService);
  bpca = inject(BpcaService);

  dev: boolean = true;

  apcaToWcagLookup: NumberKeyLookup = {};
  apcaToWcagLookupAlt: NumberKeyLookup = {};

  getContrast(
    colorOne: string,
    colorTwo: string,
    contrastType: ContrastType
  ): number | null {
    let score: number | null = null;

    const contrast = this.calcRawApcaContrast(colorOne, colorTwo);

    if (contrast || contrast === 0) {
      if (contrastType === 'apca') {
        const roundedContrast = contrast.toFixed(0);

        score = parseInt(roundedContrast);
      }

      if (contrastType === 'bpca') {
        const bpcaScore = this.calcRawBpcaContrast(colorOne, colorTwo);

        score = bpcaScore;
      }
    } else {
      console.error(`Raw APCA contrast was not calculable`);
    }

    return score;
  }

  calcRawApcaContrast(colorOne: string, colorTwo: string): number | null {
    let score: number | null = null;

    score = calcAPCA(colorOne, colorTwo);

    return score;
  }

  calcRawBpcaContrast(colorOne: string, colorTwo: string) {
    let wcag: number = NaN;

    const lc = this.bpca.calcBPCA(colorOne, colorTwo) as number;
    const colorOneArray255 = this.cus.getRgb255Array(colorOne);
    const colorTwoArray255 = this.cus.getRgb255Array(colorTwo);

    if (colorOneArray255 && colorTwoArray255) {
      const colorOneY = this.bpca.sRGBtoY(colorOneArray255);
      const colorTwoY = this.bpca.sRGBtoY(colorTwoArray255);

      wcag = parseFloat(this.bpca.bridgeRatio(lc, colorOneY, colorTwoY, ''));
    } else {
      console.warn(`trouble calculating raw BPCA`);
    }

    return wcag;
  }
}

import {
  Component,
  Input,
  OnChanges,
  OnInit,
  Output,
  EventEmitter,
  SimpleChanges,
  inject,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { ColorUtilService } from '../services/color-util.service';

export interface ResetObject {
  reset: true;
}

@Component({
  selector: 'app-color-slider',
  templateUrl: './color-slider.component.html',
  styleUrls: ['./color-slider.component.scss'],
  imports: [CommonModule],
  standalone: true,
})
export class ColorSliderComponent implements OnInit, OnChanges {
  @Input() id: string | 'slider-0' = 'slider-0';
  @Input() name: string | 'color-slider' = 'color-slider';
  @Input() color: string | null = null;
  @Input() constantChroma: boolean = false;
  @Input() showGradient: boolean = false;
  @Input() resetSlider: ResetObject | null = null;
  @Output() colorVariant = new EventEmitter<string | null>();

  cus = inject(ColorUtilService);

  debug: boolean = false;
  devColorVariant: string | null = null;

  slideInterval: number | null = null;
  slideMin: number | null = null;
  slideMax: number | null = null;
  initValue: number | null = null;
  value: number | null = null;

  getInitValue() {
    return this.initValue;
  }

  sendInitialLightVariant() {
    // Good UX to just send the input color?
    this.colorVariant.emit(this.color);

    if (this.debug) {
      this.devColorVariant = this.color;
    }
  }

  async getAndSetLightnessRange(
    color: string,
    options?: { constantChroma: boolean }
  ) {
    const rangeObject = await this.cus.getMinMaxLight(color);

    if (rangeObject) {
      this.sendInitialLightVariant();

      this.slideMin = 0;
      this.slideMax = 1;

      if (options?.constantChroma) {
        this.slideMin = rangeObject.lightMin;

        this.slideMax = rangeObject.lightMax;
      }

      this.slideInterval = (this.slideMax - this.slideMin) / 80;

      const lightnessIndex = 0;
      const initialSlideValue = rangeObject.originalCoords[lightnessIndex];

      this.initValue = initialSlideValue;

      this.value = initialSlideValue;

      this.redefineGradientStops(this.slideMin, this.slideMax);
    } else {
      console.error(`no range object for slider`);
    }
  }

  handleSliding(event: Event) {
    const inputElem = event.target as HTMLInputElement;

    if (inputElem) {
      const lightValue = parseFloat(inputElem.value);

      if (this.color) {
        const lightnessVariant = this.cus.createSrgbColor(
          this.color,
          lightValue
        );

        if (this.debug) {
          console.log(`slide modding ${this.color} to ${lightnessVariant}`);

          this.devColorVariant = lightnessVariant;
        }

        this.colorVariant.emit(lightnessVariant);
      } else {
        console.error(`no color specified`);
      }
    }
  }

  reset() {
    // TODO: Isn't there an angular way to do this?
    const element = document.getElementById(this.id) as HTMLInputElement;

    if (this.initValue) {
      element.value = this.initValue.toString();
    } else {
      console.error(`trouble resetting slider`);
    }
  }

  gradient(val: 'on' | 'off') {
    // TODO: Anguar way to do this?
    const targetElem = document.getElementById(`cc-${this.id}`) as HTMLElement;

    if (targetElem) {
      if (val === 'on') {
        targetElem.style.background = 'var(--gradient-background)';
      }

      if (val === 'off') {
        targetElem.style.background = 'var(--default-background)';
      }
    } else {
      console.warn(`no elem to assign gradient to.`);
    }
  }

  redefineVariable(
    element: HTMLElement,
    variableName: string,
    newValue: string
  ) {
    element.style.setProperty(variableName, newValue);
  }

  redefineGradientStops(lightMin: number, lightMax: number) {
    if (this.color) {
      const targetElement = document.getElementById(
        `cc-${this.id}`
      ) as HTMLElement;

      const stops = [
        '--grad-stop-0',
        '--grad-stop-1',
        '--grad-stop-2',
        '--grad-stop-3',
        '--grad-stop-4',
        '--grad-stop-5',
      ];

      // get new stop values
      const stopInterval = (lightMax - lightMin) / (stops.length - 1);
      const stopVals = [];

      for (let i = 0; i < stops.length; i++) {
        const targetLight = stopInterval * i + lightMin;
        targetLight;

        const stopColor = this.cus.createSrgbColor(this.color, targetLight);

        stopVals.push(stopColor);
      }

      // assign new stop values

      for (let i = 0; i < stops.length; i++) {
        const targetStop = stops[i];
        const targetStopVal = stopVals[i];

        if (targetStopVal) {
          this.redefineVariable(targetElement, targetStop, targetStopVal);
        } else {
          console.log(`no new val to assign`);
        }
      }
    } else {
      console.log(`no color specified`);
    }
  }

  ngOnInit(): void {}

  ngOnChanges(changes: SimpleChanges): void {
    if (this.color) {
      this.getAndSetLightnessRange(this.color, {
        constantChroma: this.constantChroma,
      });
    } else {
      console.warn(`no color specified to comp`);
    }

    if (this.showGradient) {
      this.gradient('on');
    }

    if (!this.showGradient) {
      this.gradient('off');
    }

    if (this.resetSlider) {
      this.reset();
    }
  }
}

