      // 1) create enough steps of lightness
      const lightnessSteps = 1000;
      const lightMax = 1;
      const lightMin = 0;
      const lightInterval = (lightMax - lightMin) / lightnessSteps;

      // 2) create all variants of color using constant chroma and hue.
      variantCollection = [];

      // 2a) this actually creates `lightnessSteps + 1` variants as initial variant has to start at zero.
      for (let i = 0; i <= lightnessSteps; i++) {
        const variantTargetLight = i * lightInterval;
        const variant: ColorCoordArray = [variantTargetLight, colorChroma, colorHue];

        variantCollection.push(variant);
      }
    } else {
      console.error(`unable to parse color`);
    }

    return variantCollection;
  }

  filterOutOfGamutVariants(
    variants: Array<ColorCoordArray> | null
  ): Promise<Array<ColorCoordArray>> {
    return new Promise(async (resolve, reject) => {
      if (!variants) {
        reject(`no variants`);
      } else {
        let filteringComplete: boolean = false;

        const filtered = [];

        for (let i = 0; i < variants.length; i++) {
          const curVariant = variants[i];

          if (await this.isInSrgbGamut(curVariant)) {
            filtered.push(curVariant);
          }

          if (i === variants.length - 1) filteringComplete = true;
        }

        if (filteringComplete) {
          resolve(filtered);
        } else {
          reject(`error`);
        }
      }
    });
  }

  async getMinMaxLight(color: string): Promise<MinMaxLightObject | null> {
    let returnedObject: MinMaxLightObject | null = null;

    const initVariantCollection = this.createVariants(color);

    const variantCollection = await this.filterOutOfGamutVariants(initVariantCollection);

    const parsedColor = this.parseColor(color);

    if (parsedColor && variantCollection.length) {
      const oklchColor = Color.to(parsedColor, 'oklch');

      const lchCooords = oklchColor.coords;

      const oklchLightCoordIndex = 0;
      const firstArrayItemIndex = 0;
      const lastArrayItemIndex = variantCollection.length - 1;

      const minLight = variantCollection[firstArrayItemIndex][oklchLightCoordIndex];
      const maxLight = variantCollection[lastArrayItemIndex][oklchLightCoordIndex];

      returnedObject = {
        originalCoords: lchCooords,
        lightMin: minLight,
        lightMax: maxLight,
      };
    } else {
      if (!parsedColor) {
        console.error(`unable to parse color`);
      } else {
        const oklchColor = Color.to(parsedColor, 'oklch');

        const lchCooords = oklchColor.coords;
        returnedObject = {
          originalCoords: lchCooords,
          lightMin: lchCooords[0],
          lightMax: lchCooords[0],
        };
      }
    }

    return returnedObject;
  }

  async getRandomColorPair(): Promise<ColorPair> {
    let pair: ColorPair = ['black', 'white'];

    // ref: oklch.com
    // const targetChroma = 0.11;
    const targetChroma = random(0.11, 0.34, true);

    const colorOneLight = random(0.25, 0.26, true);
    const colorOneHue = random(0, 360, true);
    const colorTwoLight = random(0.94, 0.95, true);
    const colorTwoHue = random(0, 360, true);

    const colorOne = new Color('oklch', [colorOneLight, targetChroma, colorOneHue])
      .toGamut({ space: 'srgb', method: 'oklch.c' })
      .to('srgb')
      .toString({ format: 'hex' });

    const colorTwo = new Color('oklch', [colorTwoLight, targetChroma, colorTwoHue])
      .toGamut({ space: 'srgb', method: 'oklch.c' })
      .to('srgb')
      .toString({ format: 'hex' });

    const initPair: ColorPair = [colorOne, colorTwo];

    const chromaMatchedPair = await this.matchChromas(initPair);

    pair = chromaMatchedPair.colors ? chromaMatchedPair.colors : pair;

    return pair;
  }

  // This function only adjust the first color of the pair.
  async adjustColorPairForPresentation(pair: ColorPair): Promise<ColorPair> {
    let returnedPair: ColorPair = ['black', 'white'];

    const colorOne = pair[0];
    const colortwo = pair[1];

    const parsedColorOne = this.parseColor(colorOne);

    const colorOneMinMaxLightObj = await this.getMinMaxLight(colorOne);

    if (parsedColorOne && colorOneMinMaxLightObj) {
      const colorOneTargetLightness =
        colorOneMinMaxLightObj.lightMin +
        (colorOneMinMaxLightObj.lightMax - colorOneMinMaxLightObj.lightMin) / 2;

      const oklchColorOne = new Color('srgb', parsedColorOne.coords).to('oklch');

      const adjColorOne = new Color('oklch', [
        colorOneTargetLightness,
        oklchColorOne.coords[1],
        oklchColorOne.coords[2],
      ])
        .to('srgb')
        .toString({ format: 'hex' });

      returnedPair = [adjColorOne, colortwo];
    } else {
      console.warn('trouble adjusting colors');
    }

    return returnedPair;
  }

  async matchChromas(colorpair: ColorPair): Promise<ChromaMatchObject> {
    let pair: ChromaMatchObject = new ChromaMatchObject();

    const colorOneParsed = this.parseColor(colorpair[0]);
    const colorTwoParsed = this.parseColor(colorpair[1]);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneOklch = new Color('srgb', colorOneParsed.coords).to('oklch');
      const colorOneChroma = colorOneOklch.coords[1];

      const colorTwoOklch = new Color('srgb', colorTwoParsed.coords).to('oklch');
      const colorTwoChroma = colorTwoOklch.coords[1];

      const colorOneCandCoords: ColorCoordArray = [
        colorOneOklch.coords[0],
        colorTwoChroma,
        colorOneOklch.coords[2],
      ];
      const colorTwoCandCoords: ColorCoordArray = [
        colorTwoOklch.coords[0],
        colorOneChroma,
        colorTwoOklch.coords[2],
      ];

      const colorOneCandInGamut = await this.isInSrgbGamut(colorOneCandCoords);
      const colorTwoCandInGamut = await this.isInSrgbGamut(colorTwoCandCoords);

      if (colorOneCandInGamut && !colorTwoCandInGamut) {
        pair.success = true;

        pair.colors = [
          new Color('oklch', colorOneCandCoords).to('srgb').toString({ format: 'hex' }),
          colorpair[1],
        ];

        pair.chroma = colorOneCandCoords[1];
      }

      if (!colorOneCandInGamut && colorTwoCandInGamut) {
        pair.success = true;

        pair.colors = [
          colorpair[0],
          new Color('oklch', colorTwoCandCoords).to('srgb').toString({ format: 'hex' }),
        ];

        pair.chroma = colorTwoCandCoords[1];
      }

      if (colorOneCandInGamut && colorTwoCandInGamut) {
        if (colorOneCandCoords[1] > colorTwoCandCoords[1]) {
          pair.success = true;

          pair.colors = [
            new Color('oklch', colorOneCandCoords).to('srgb').toString({ format: 'hex' }),
            colorpair[1],
          ];

          pair.chroma = colorTwoCandCoords[1];
        } else {
          pair.success = true;

          pair.colors = [
            colorpair[0],
            new Color('oklch', colorTwoCandCoords).to('srgb').toString({ format: 'hex' }),
          ];

          pair.chroma = colorTwoCandCoords[1];
        }
      }
    } else {
      console.error("color parsing didn't work out. ");
    }

    return pair;
  }

  calcDeltaE(colorOne: string, colorTwo: string): number | null {
    let delta: number | null = null;

    const colorOneParsed = this.parseColor(colorOne);
    const colorTwoParsed = this.parseColor(colorTwo);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneObj = new Color('srgb', colorOneParsed.coords);
      const colorTwoObj = new Color('srgb', colorTwoParsed.coords);

      const rawDelta = colorOneObj.deltaE2000(colorTwoObj);

      // const fixedDelta = rawDelta.toFixed(2);

      // delta = parseFloat(fixedDelta);
      delta = Math.round(rawDelta);
    }

    return delta;
  }

  calcWcag2(colorOne: string, colorTwo: string): number | null {
    let wcag21: number | null = null;

    const colorOneParsed = this.parseColor(colorOne);
    const colorTwoParsed = this.parseColor(colorTwo);

    if (colorOneParsed && colorTwoParsed) {
      const colorOneObj = new Color('srgb', colorOneParsed.coords);
      const colorTwoObj = new Color('srgb', colorTwoParsed.coords);

      const rawWcag21 = colorOneObj.contrast(colorTwoObj, 'WCAG21');

      const fixedDelta = rawWcag21.toFixed(1);

      wcag21 = parseFloat(fixedDelta);
    }

    return wcag21;
  }

  getColorMeta(color: string): ColorMetaObj | null {
    let meta: ColorMetaObj | null = null;

    const parsedColor = this.parseColor(color);

    if (parsedColor) {
      const lchColor = Color.to(parsedColor, 'oklch');

      meta = {
        lightness: lchColor.coords[0].toFixed(2),
        chroma: lchColor.coords[1].toFixed(2),
        hue: lchColor.coords[2].toFixed(2),
        saturation: ((lchColor.coords[1] / lchColor.coords[0]) * 100).toFixed(2),
      };
    }

    return meta;
  }

  getMinObjectDimension(apca: number): number {
    let dimension: number = NaN;

    const absApca = Math.abs(apca);

    const pixels = [1, 1.5, 2, 3, 4, 6, 8, 10, 15];
    const apcaScores = [90, 75, 60, 50, 45, 30, 25, 20, 15];
    const minLookup = scaleLinear(pixels).domain(apcaScores);

    const initSize = minLookup(absApca).toFixed(2);

    dimension = parseFloat(initSize);

    if (dimension > 15) {
      dimension = 15;
    }

    if (absApca >= 100) {
      dimension = 0.25;
    }

    if (absApca < 15) {
      dimension = NaN;
    }

    return dimension;
  }

  generateAllOklchVariants(
    color: string,
    lightSteps: number,
    chromaSteps: number
  ): Promise<TableData> {
    return new Promise((resolve, reject) => {
      const parsedColor = this.parseColor(color);

      const variantsCollection = [];

      let sortedVariantsCollection = [];

      if (parsedColor) {
        const oklchColor = Color.to(parsedColor, 'oklch');
        const lchCooords = oklchColor.coords;
        const colorLight = lchCooords[0];
        const colorChroma = lchCooords[1];
        const colorHue = lchCooords[2];

        const lightMax = 1;
        const lightMin = 0;
        const lightInterval = (lightMax - lightMin) / lightSteps;

        // calculate light levels
        const rawLightLevels = [];

        let lLevel = colorLight;
        do {
          rawLightLevels.push(lLevel);

          lLevel = lLevel + lightInterval;
        } while (lLevel <= lightMax);

        lLevel = colorLight;

        do {
          rawLightLevels.push(lLevel);

          lLevel = lLevel - lightInterval;
        } while (lLevel >= lightMin);

        const sortedLightLevels = sort(uniq(rawLightLevels));

        const chromaMax = 0.33;
        const chromaMin = 0;
        const chromaInterval = (chromaMax - chromaMin) / chromaSteps;

        // calculate chroma levels
        const rawChromaLevels = [];

        let cLevel = colorChroma;
        do {
          rawChromaLevels.push(cLevel);

          cLevel = cLevel + chromaInterval;
        } while (cLevel <= chromaMax);

        cLevel = colorChroma;

        do {
          rawChromaLevels.push(cLevel);

          cLevel = cLevel - chromaInterval;
        } while (cLevel >= chromaMin);

        const sortedChromaLevels = sort(uniq(rawChromaLevels));

        // generate all rows
        for (let i = 0; i < sortedLightLevels.length; i++) {
          const variantRow: Array<TableColorCell> = [];

          for (let j = 0; j < sortedChromaLevels.length; j++) {
            const targetLightness = sortedLightLevels[i];

            const targetChroma = sortedChromaLevels[j];

            const variantColor = new Color('oklch', [targetLightness, targetChroma, colorHue]);

            const variantColorinGamut = variantColor.inGamut('srgb');

            const colorVal = variantColorinGamut
              ? variantColor.to('srgb').toString({ format: 'hex' })
              : '';

            const deltaE = colorVal ? this.calcDeltaE(colorVal, color) : null;

            const dLight = colorVal
              ? Math.round(((targetLightness - colorLight) / colorLight) * 100)
              : null;

            const dChroma = colorVal
              ? Math.round(((targetChroma - colorChroma) / colorChroma) * 100)
              : null;

            const variantObj: TableColorCell = {
              color: colorVal,
              lightness: targetLightness,
              chroma: targetChroma,
              hue: colorHue,
              deltaE: !deltaE ? NaN : deltaE,
              deltaChroma: !dChroma ? NaN : dChroma,
              deltaLightness: !dLight ? NaN : dLight,
            };

            variantRow.push(variantObj);
          }

          variantsCollection.push(variantRow);
        }
      } else {
        console.error(`could not parse color`);

        reject(`could not parse color`);
      }

      // order color rows from light to dark
      sortedVariantsCollection = reverse(variantsCollection);

      resolve(sortedVariantsCollection);
    });
  }

  constructor() {}
}

import { Component, effect, inject, input, signal } from '@angular/core';
import { ColorMetricsService, ContrastType } from '../../services/color-metrics.service';
import { ColorUtilService } from '../../services/color-util.service';

export class ContrastObject {
  score: number = NaN;
  type: ContrastType | null = null;
}

@Component({
  selector: 'app-color-contrast',
  templateUrl: './color-contrast.component.html',
  styleUrls: ['./color-contrast.component.scss'],
  standalone: true,
})
export class ColorContrastComponent {
  cus = inject(ColorUtilService);
  cms = inject(ColorMetricsService);

  colorOne = input<string>('');
  colorTwo = input<string>('');
  contrastType = input<ContrastType | 'apca object'>('apca');
  debug = input<boolean>(false);

  contrastScore = signal<number>(NaN);

  constructor() {
    effect(() => {
      const colorOne = this.colorOne();
      const colorTwo = this.colorTwo();
      const contrastType = this.contrastType();
      const debug = this.debug();

      if (!colorOne || !colorTwo || !contrastType) {
        if (debug) {
          console.warn('contrast comp missing inputs');
        }

        return;
      }

      const isApcaLike = contrastType.search('apca') > -1 ? true : false;

      const score = this.cms.getContrast(colorOne, colorTwo, isApcaLike ? 'apca' : 'bpca');

      if (score && contrastType === 'apca object') {
        this.contrastScore.set(this.cus.getMinObjectDimension(score));

        return;
      }

      this.contrastScore.set(!score ? NaN : score);
    });
  }
}

import { Component, Output, EventEmitter, input, output } from '@angular/core';

export class CopyToClipboardEvent {
  copied: boolean = false;
  color: string = '';
}

@Component({
  selector: 'app-copy-to-clipboard-button',
  templateUrl: './copy-to-clipboard-button.component.html',
  styleUrls: ['./copy-to-clipboard-button.component.scss'],
  standalone: true,
})
export class CopyToClipboardButtonComponent {
  color = input<string>('');
  debug = input<boolean>(true);

  copyEvent = output<CopyToClipboardEvent>();

  async copyToClipboard(): Promise<void> {
    const color = this.color();

    if (!color) {
      if (this.debug()) {
        console.warn('no color provided to copy');
      }

      return;
    }

    const colorSansHex = color.replace('#', '');

    try {
      await navigator.clipboard.writeText(colorSansHex);

      this.copyEvent.emit({
        copied: true,
        color: colorSansHex,
      });

      if (this.debug()) {
        console.log(color, 'copied to clipboard');
      }
    } catch (err) {
      this.copyEvent.emit({
        copied: false,
        color: colorSansHex,
      });

      console.error('Failed to copy text: ', err);
    }
  }
}

import { Component, effect, inject, input, signal, computed } from '@angular/core';
import { ColorUtilService, ColorMetaObj } from '../../services/color-util.service';
import { ColorMetricsService } from '../../services/color-metrics.service';

export class DifferencesDataObj {
  deltaE: number = NaN;
  wcag2Old: number = NaN;
  wcag2New: number = NaN;
  apca: number = NaN;
}
export class SuccessesObj {
  text: 'pass' | 'fail' | null = null;
  largeText: 'pass' | 'fail' | null = null;
  objectMinDimension: number | string = NaN;
}

@Component({
  selector: 'app-metadata',
  templateUrl: './metadata.component.html',
  styleUrls: ['./metadata.component.scss'],
  standalone: true,
})
export class MetadataComponent {
  colorOne = input<string>('');
  colorTwo = input<string>('');
  debug = input<boolean>(false);

  private readonly cus = inject(ColorUtilService);
  private readonly cms = inject(ColorMetricsService);

  readonly colorOneMeta = computed<ColorMetaObj | null>(() => {
    const colOne = this.colorOne();
    if (!colOne) {
      if (this.debug()) {
        console.warn('failed to get colorOne meta');
      }
      return null;
    }
    return this.cus.getColorMeta(colOne) ?? null;
  });

  readonly colorTwoMeta = computed<ColorMetaObj | null>(() => {
    const colTwo = this.colorTwo();
    if (!colTwo) {
      if (this.debug()) {
        console.warn('failed to get colorTwo meta');
      }
      return null;
    }
    return this.cus.getColorMeta(colTwo) ?? null;
  });

  readonly differences = computed<DifferencesDataObj>(() => {
    const colOne = this.colorOne();
    const colTwo = this.colorTwo();
    const result = new DifferencesDataObj();
    if (!colOne || !colTwo) {
      if (this.debug()) {
        console.warn('failed to get color differences');
      }
      return result;
    }
    const deltaE = this.cus.calcDeltaE(colOne, colTwo);
    const wcag2New = this.cms.getContrast(colOne, colTwo, 'bpca');
    const wcag2Old = this.cus.calcWcag2(colOne, colTwo);
    const apca = this.cms.getContrast(colOne, colTwo, 'apca');
    result.deltaE = !deltaE ? NaN : deltaE;
    result.wcag2New = !wcag2New ? NaN : wcag2New;
    result.wcag2Old = !wcag2Old ? NaN : wcag2Old;
    result.apca = !apca ? NaN : apca;
    return result;
  });

  readonly successes = computed<SuccessesObj>(() => {
    const colOne = this.colorOne();
    const colTwo = this.colorTwo();
    const result: SuccessesObj = {
      text: null,
      largeText: null,
      objectMinDimension: NaN,
    };
    if (!colOne || !colTwo) {
      if (this.debug()) {
        console.warn('no colors for successes');
      }
      return result;
    }
    const wcagNew = this.cms.getContrast(colOne, colTwo, 'bpca');
    const apcaScore = this.cms.getContrast(colOne, colTwo, 'apca');
    if (!wcagNew || !apcaScore) {
      if (this.debug()) {
        console.warn('trouble getting scores');
      }
      return result;
    }
    if (wcagNew < 0 || Math.abs(apcaScore) < 0) {
      if (this.debug()) {
        console.warn('something wonky with calculating scores');
      }
      return result;
    }
    result.text = wcagNew >= 4.5 ? 'pass' : 'fail';
    result.largeText = wcagNew >= 3 ? 'pass' : 'fail';
    const minDimension = this.cus.getMinObjectDimension(apcaScore);
    result.objectMinDimension = Number.isNaN(minDimension) ? 'invisible' : minDimension;
    return result;
  });
}

